
    var current = 0;
             
    function g_prev() {
    current = (current - 1>=0)?current-1:5;
    g_toggle(current);
    }
             
    function g_next() {
    current = (current + 1 <= 5)?current+1:0;
    g_toggle(current);
    }
            
    function g_toggle(i) {
    var b = i;
    $('#gallery').find('img.qr:visible').fadeOut(10, function() { $($('#gallery').find('img.qr').get(b)).fadeIn(10); });
    $('#gallery').find('span.qr:visible').fadeOut(10, function() { $($('#gallery').find('span.qr').get(b)).fadeIn(10); });
    }
